SELECT date_dim.d_year, item.i_brand_id AS brand_id, item.i_brand AS brand, SUM(store_sales.ss_sales_price) AS sum_agg FROM date_dim, store_sales, item WHERE date_dim.d_date_sk = store_sales.ss_sold_date_sk AND store_sales.ss_item_sk = item.i_item_sk AND item.i_manufact_id = 816 AND date_dim.d_moy = 11 GROUP BY date_dim.d_year, item.i_brand, item.i_brand_id ORDER BY date_dim.d_year, SUM(store_sales.ss_sales_price) DESC, item.i_brand_id LIMIT 100