SELECT t.i_product_name FROM (SELECT * FROM item WHERE i_manufact_id >= 704 AND i_manufact_id <= 704 + 40) AS t INNER JOIN (SELECT * FROM (SELECT t1.i_manufact AS i_manufact0, COUNT(*) AS item_cnt FROM item AS item0 INNER JOIN (SELECT i_manufact FROM item WHERE i_manufact_id >= 704 AND i_manufact_id <= 704 + 40) AS t1 ON item0.i_manufact = t1.i_manufact AND (item0.i_category = 'Women' AND (item0.i_color = 'forest' OR item0.i_color = 'lime') AND (item0.i_units = 'Pallet' OR item0.i_units = 'Pound') AND (item0.i_size = 'economy' OR item0.i_size = 'small') OR item0.i_category = 'Women' AND (item0.i_color = 'navy' OR item0.i_color = 'slate') AND CAST(item0.i_units AS CHAR(5)) IN ('Bunch', 'Gross') AND (item0.i_size = 'extra large' OR item0.i_size = 'petite') OR item0.i_category = 'Men' AND (item0.i_color = 'powder' OR item0.i_color = 'sky') AND (item0.i_units = 'Dozen' OR item0.i_units = 'Lb') AND (item0.i_size = 'N/A' OR item0.i_size = 'large') OR item0.i_category = 'Men' AND (item0.i_color = 'maroon' OR item0.i_color = 'smoke') AND (item0.i_units = 'Ounce' OR item0.i_units = 'Case') AND (item0.i_size = 'economy' OR item0.i_size = 'small')) OR item0.i_manufact = t1.i_manufact AND (item0.i_category = 'Women' AND (item0.i_color = 'dark' OR item0.i_color = 'aquamarine') AND CAST(item0.i_units AS CHAR(3)) IN ('Tbl', 'Ton') AND (item0.i_size = 'economy' OR item0.i_size = 'small') OR item0.i_category = 'Women' AND (item0.i_color = 'frosted' OR item0.i_color = 'plum') AND (item0.i_units = 'Dram' OR item0.i_units = 'Box') AND (item0.i_size = 'extra large' OR item0.i_size = 'petite') OR item0.i_category = 'Men' AND (item0.i_color = 'papaya' OR item0.i_color = 'peach') AND CAST(item0.i_units AS CHAR(6)) IN ('Bundle', 'Carton') AND (item0.i_size = 'N/A' OR item0.i_size = 'large') OR item0.i_category = 'Men' AND (item0.i_color = 'firebrick' OR item0.i_color = 'sienna') AND (item0.i_units = 'Cup' OR item0.i_units = 'Each') AND (item0.i_size = 'economy' OR item0.i_size = 'small')) GROUP BY t1.i_manufact) AS t4 WHERE t4.item_cnt > 0) AS t5 ON t.i_manufact = t5.i_manufact0 ORDER BY t.i_product_name LIMIT 100