SELECT date_dim.d_year, item.i_category_id, item.i_category, SUM(store_sales.ss_ext_sales_price) FROM date_dim, store_sales, item WHERE date_dim.d_date_sk = store_sales.ss_sold_date_sk AND store_sales.ss_item_sk = item.i_item_sk AND item.i_manager_id = 1 AND date_dim.d_moy = 11 AND date_dim.d_year = 1998 GROUP BY date_dim.d_year, item.i_category_id, item.i_category ORDER BY SUM(store_sales.ss_ext_sales_price) DESC, date_dim.d_year, item.i_category_id, item.i_category LIMIT 100