SELECT store.s_store_name, store.s_store_id, SUM(CASE WHEN date_dim.d_day_name = 'Sunday' THEN store_sales.ss_sales_price ELSE NULL END) AS sun_sales, SUM(CASE WHEN date_dim.d_day_name = 'Monday' THEN store_sales.ss_sales_price ELSE NULL END) AS mon_sales, SUM(CASE WHEN date_dim.d_day_name = 'Tuesday' THEN store_sales.ss_sales_price ELSE NULL END) AS tue_sales, SUM(CASE WHEN date_dim.d_day_name = 'Wednesday' THEN store_sales.ss_sales_price ELSE NULL END) AS wed_sales, SUM(CASE WHEN date_dim.d_day_name = 'Thursday' THEN store_sales.ss_sales_price ELSE NULL END) AS thu_sales, SUM(CASE WHEN date_dim.d_day_name = 'Friday' THEN store_sales.ss_sales_price ELSE NULL END) AS fri_sales, SUM(CASE WHEN date_dim.d_day_name = 'Saturday' THEN store_sales.ss_sales_price ELSE NULL END) AS sat_sales FROM date_dim, store_sales, store WHERE date_dim.d_date_sk = store_sales.ss_sold_date_sk AND store.s_store_sk = store_sales.ss_store_sk AND store.s_gmt_offset = -5 AND date_dim.d_year = 2000 GROUP BY store.s_store_name, store.s_store_id ORDER BY store.s_store_name, store.s_store_id, SUM(CASE WHEN date_dim.d_day_name = 'Sunday' THEN store_sales.ss_sales_price ELSE NULL END), SUM(CASE WHEN date_dim.d_day_name = 'Monday' THEN store_sales.ss_sales_price ELSE NULL END), SUM(CASE WHEN date_dim.d_day_name = 'Tuesday' THEN store_sales.ss_sales_price ELSE NULL END), SUM(CASE WHEN date_dim.d_day_name = 'Wednesday' THEN store_sales.ss_sales_price ELSE NULL END), SUM(CASE WHEN date_dim.d_day_name = 'Thursday' THEN store_sales.ss_sales_price ELSE NULL END), SUM(CASE WHEN date_dim.d_day_name = 'Friday' THEN store_sales.ss_sales_price ELSE NULL END), SUM(CASE WHEN date_dim.d_day_name = 'Saturday' THEN store_sales.ss_sales_price ELSE NULL END) LIMIT 100