SELECT * FROM (SELECT t.i_manufact_id, SUM(store_sales.ss_sales_price) AS sum_sales, (SUM(SUM(store_sales.ss_sales_price)) OVER (PARTITION BY t.i_manufact_id RANGE BETWEEN UNBOUNDED PRECEDING AND UNBOUNDED FOLLOWING)) / (COUNT(SUM(store_sales.ss_sales_price)) OVER (PARTITION BY t.i_manufact_id RANGE BETWEEN UNBOUNDED PRECEDING AND UNBOUNDED FOLLOWING)) AS avg_quarterly_sales FROM (SELECT * FROM item WHERE i_category IN ('Books', 'Children', 'Electronics') AND i_class IN ('personal', 'portable', 'reference', 'self-help') AND i_brand IN ('exportiunivamalg #9', 'scholaramalgamalg #14', 'scholaramalgamalg #7', 'scholaramalgamalg #9') OR i_category IN ('Men', 'Music', 'Women') AND i_class IN ('accessories', 'classical', 'fragrances', 'pants') AND i_brand IN ('amalgimporto #1', 'edu packscholar #1', 'exportiimporto #1', 'importoamalg #1')) AS t INNER JOIN store_sales ON t.i_item_sk = store_sales.ss_item_sk INNER JOIN (SELECT * FROM date_dim WHERE d_month_seq = 1186 OR (d_month_seq = 1186 + 1 OR d_month_seq = 1186 + 2) OR (d_month_seq = 1186 + 3 OR (d_month_seq = 1186 + 4 OR d_month_seq = 1186 + 5)) OR (d_month_seq = 1186 + 6 OR (d_month_seq = 1186 + 7 OR d_month_seq = 1186 + 8) OR (d_month_seq = 1186 + 9 OR (d_month_seq = 1186 + 10 OR d_month_seq = 1186 + 11)))) AS t0 ON store_sales.ss_sold_date_sk = t0.d_date_sk INNER JOIN store ON store_sales.ss_store_sk = store.s_store_sk GROUP BY t.i_manufact_id, t0.d_qoy) AS t2 WHERE CASE WHEN t2.avg_quarterly_sales > 0 THEN CAST(ABS(t2.sum_sales - t2.avg_quarterly_sales) / t2.avg_quarterly_sales AS DECIMAL(19, 0)) ELSE NULL END > 0.1 ORDER BY avg_quarterly_sales, sum_sales, i_manufact_id LIMIT 100