SELECT item.i_brand_id AS brand_id, item.i_brand AS brand, SUM(store_sales.ss_ext_sales_price) AS ext_price FROM date_dim, store_sales, item WHERE date_dim.d_date_sk = store_sales.ss_sold_date_sk AND store_sales.ss_item_sk = item.i_item_sk AND item.i_manager_id = 52 AND date_dim.d_moy = 11 AND date_dim.d_year = 2000 GROUP BY item.i_brand, item.i_brand_id ORDER BY SUM(store_sales.ss_ext_sales_price) DESC, item.i_brand_id LIMIT 100