SELECT t0.i_brand_id AS brand_id, t0.i_brand AS brand, SUM(store_sales.ss_ext_sales_price) AS ext_price FROM (SELECT * FROM date_dim WHERE d_moy = 11 AND d_year = 2000) AS t INNER JOIN store_sales ON t.d_date_sk = store_sales.ss_sold_date_sk INNER JOIN (SELECT * FROM item WHERE i_manager_id = 52) AS t0 ON store_sales.ss_item_sk = t0.i_item_sk GROUP BY t0.i_brand, t0.i_brand_id ORDER BY SUM(store_sales.ss_ext_sales_price) DESC, t0.i_brand_id LIMIT 100