SELECT t1.promotions, t4.total, CAST(t1.promotions AS DECIMAL(15, 4)) / CAST(t4.total AS DECIMAL(15, 4)) * 100 FROM (SELECT SUM(store_sales.ss_ext_sales_price) AS promotions FROM store_sales, store, promotion, date_dim, customer, customer_address, item WHERE store_sales.ss_sold_date_sk = date_dim.d_date_sk AND (store_sales.ss_store_sk = store.s_store_sk AND store_sales.ss_promo_sk = promotion.p_promo_sk) AND (store_sales.ss_customer_sk = customer.c_customer_sk AND (customer_address.ca_address_sk = customer.c_current_addr_sk AND store_sales.ss_item_sk = item.i_item_sk)) AND (customer_address.ca_gmt_offset = -7 AND (item.i_category = 'Home' AND (promotion.p_channel_dmail = 'Y' OR promotion.p_channel_email = 'Y' OR promotion.p_channel_tv = 'Y')) AND (store.s_gmt_offset = -7 AND (date_dim.d_year = 2000 AND date_dim.d_moy = 12)))) AS t1, (SELECT SUM(store_sales0.ss_ext_sales_price) AS total FROM store_sales AS store_sales0, store AS store0, date_dim AS date_dim0, customer AS customer0, customer_address AS customer_address0, item AS item0 WHERE store_sales0.ss_sold_date_sk = date_dim0.d_date_sk AND store_sales0.ss_store_sk = store0.s_store_sk AND (store_sales0.ss_customer_sk = customer0.c_customer_sk AND (customer_address0.ca_address_sk = customer0.c_current_addr_sk AND store_sales0.ss_item_sk = item0.i_item_sk)) AND (customer_address0.ca_gmt_offset = -7 AND item0.i_category = 'Home' AND (store0.s_gmt_offset = -7 AND (date_dim0.d_year = 2000 AND date_dim0.d_moy = 12)))) AS t4 ORDER BY t1.promotions, t4.total LIMIT 100