SELECT store.s_store_name, item.i_item_desc, t6.revenue, item.i_current_price, item.i_wholesale_cost, item.i_brand FROM store CROSS JOIN item CROSS JOIN (SELECT t2.ss_store_sk, AVG(t2.revenue) AS ave FROM (SELECT store_sales.ss_store_sk, SUM(store_sales.ss_sales_price) AS revenue FROM store_sales INNER JOIN (SELECT * FROM date_dim WHERE d_month_seq >= 1176 AND d_month_seq <= 1176 + 11) AS t ON store_sales.ss_sold_date_sk = t.d_date_sk GROUP BY store_sales.ss_store_sk, store_sales.ss_item_sk) AS t2 GROUP BY t2.ss_store_sk) AS t3 INNER JOIN (SELECT store_sales0.ss_store_sk, store_sales0.ss_item_sk, SUM(store_sales0.ss_sales_price) AS revenue FROM store_sales AS store_sales0 INNER JOIN (SELECT * FROM date_dim WHERE d_month_seq >= 1176 AND d_month_seq <= 1176 + 11) AS t4 ON store_sales0.ss_sold_date_sk = t4.d_date_sk GROUP BY store_sales0.ss_store_sk, store_sales0.ss_item_sk) AS t6 ON t3.ss_store_sk = t6.ss_store_sk AND t6.revenue <= 0.1 * t3.ave AND store.s_store_sk = t6.ss_store_sk AND item.i_item_sk = t6.ss_item_sk ORDER BY store.s_store_name, item.i_item_desc LIMIT 100