SELECT customer_address.ca_state, COUNT(*) AS cnt FROM customer_address INNER JOIN customer ON customer_address.ca_address_sk = customer.c_current_addr_sk INNER JOIN store_sales ON customer.c_customer_sk = store_sales.ss_customer_sk INNER JOIN date_dim ON store_sales.ss_sold_date_sk = date_dim.d_date_sk INNER JOIN item ON store_sales.ss_item_sk = item.i_item_sk INNER JOIN (SELECT d_month_seq FROM date_dim WHERE d_year = 2002 AND d_moy = 3) AS t1 ON date_dim.d_month_seq = t1.d_month_seq INNER JOIN (SELECT i_category, AVG(i_current_price) AS EXPR0 FROM item GROUP BY i_category) AS t2 ON item.i_category = t2.i_category AND item.i_current_price > 1.2 * t2.EXPR0 GROUP BY customer_address.ca_state HAVING COUNT(*) >= 10 ORDER BY COUNT(*), customer_address.ca_state LIMIT 100