SELECT customer.c_last_name, customer.c_first_name, customer.c_salutation, customer.c_preferred_cust_flag, t4.ss_ticket_number, t4.cnt FROM (SELECT store_sales.ss_ticket_number, store_sales.ss_customer_sk, COUNT(*) AS cnt FROM store_sales INNER JOIN (SELECT * FROM date_dim WHERE d_dom >= 1 AND d_dom <= 2 AND (d_year = 2000 OR d_year = 2000 + 1 OR d_year = 2000 + 2)) AS t ON store_sales.ss_sold_date_sk = t.d_date_sk INNER JOIN (SELECT * FROM store WHERE s_county = 'Williamson County') AS t0 ON store_sales.ss_store_sk = t0.s_store_sk INNER JOIN (SELECT * FROM household_demographics WHERE (hd_buy_potential = '1001-5000' OR hd_buy_potential = '5001-10000') AND hd_vehicle_count > 0 AND CASE WHEN hd_vehicle_count > 0 THEN hd_dep_count / hd_vehicle_count ELSE NULL END > 1) AS t1 ON store_sales.ss_hdemo_sk = t1.hd_demo_sk GROUP BY store_sales.ss_ticket_number, store_sales.ss_customer_sk HAVING COUNT(*) >= 1 AND COUNT(*) <= 5) AS t4 INNER JOIN customer ON t4.ss_customer_sk = customer.c_customer_sk ORDER BY t4.cnt DESC, customer.c_last_name