SELECT customer.c_customer_id, customer.c_salutation, customer.c_first_name, customer.c_last_name, t2.ca_street_number, t2.ca_street_name, t2.ca_street_type, t2.ca_suite_number, t2.ca_city, t2.ca_county, t2.ca_state, t2.ca_zip, t2.ca_country, t2.ca_gmt_offset, t2.ca_location_type, t1.ctr_total_return FROM (SELECT catalog_returns.cr_returning_customer_sk AS ctr_customer_sk, customer_address.ca_state AS ctr_state, SUM(catalog_returns.cr_return_amt_inc_tax) AS ctr_total_return FROM catalog_returns INNER JOIN (SELECT * FROM date_dim WHERE d_year = 2001) AS t ON catalog_returns.cr_returned_date_sk = t.d_date_sk INNER JOIN customer_address ON catalog_returns.cr_returning_addr_sk = customer_address.ca_address_sk GROUP BY catalog_returns.cr_returning_customer_sk, customer_address.ca_state) AS t1 CROSS JOIN (SELECT * FROM customer_address WHERE ca_state = 'TN') AS t2 INNER JOIN customer ON t2.ca_address_sk = customer.c_current_addr_sk AND t1.ctr_customer_sk = customer.c_customer_sk INNER JOIN (SELECT t6.ctr_state, AVG(t6.ctr_total_return) AS f1 FROM (SELECT customer_address1.ca_state AS ctr_state, SUM(catalog_returns0.cr_return_amt_inc_tax) AS ctr_total_return FROM catalog_returns AS catalog_returns0 INNER JOIN (SELECT * FROM date_dim WHERE d_year = 2001) AS t3 ON catalog_returns0.cr_returned_date_sk = t3.d_date_sk INNER JOIN customer_address AS customer_address1 ON catalog_returns0.cr_returning_addr_sk = customer_address1.ca_address_sk GROUP BY catalog_returns0.cr_returning_customer_sk, customer_address1.ca_state) AS t6 GROUP BY t6.ctr_state) AS t7 ON t1.ctr_state = t7.ctr_state AND t1.ctr_total_return > t7.f1 * 1.2 ORDER BY customer.c_customer_id, customer.c_salutation, customer.c_first_name, customer.c_last_name, t2.ca_street_number, t2.ca_street_name, t2.ca_street_type, t2.ca_suite_number, t2.ca_city, t2.ca_county, t2.ca_state, t2.ca_zip, t2.ca_country, t2.ca_gmt_offset, t2.ca_location_type, t1.ctr_total_return LIMIT 100