SELECT t.i_item_id, t.i_item_desc, t.i_current_price FROM (SELECT * FROM item WHERE i_current_price >= 58 AND i_current_price <= 58 + 30 AND i_manufact_id IN (259, 485, 559, 580)) AS t INNER JOIN (SELECT * FROM inventory WHERE inv_quantity_on_hand >= 100 AND inv_quantity_on_hand <= 500) AS t0 ON t.i_item_sk = t0.inv_item_sk INNER JOIN (SELECT * FROM date_dim WHERE d_date >= DATE '2001-01-13' AND d_date <= (DATE '2001-01-13' + INTERVAL '60' DAY)) AS t1 ON t0.inv_date_sk = t1.d_date_sk INNER JOIN store_sales ON t.i_item_sk = store_sales.ss_item_sk ORDER BY t.i_item_id LIMIT 100