SELECT i_category, i_class, i_brand, s_store_name, s_company_name, d_moy, sum_sales, avg_monthly_sales FROM (SELECT t1.i_category, t1.i_class, t1.i_brand, t1.s_store_name, t1.s_company_name, t1.d_moy, t1.sum_sales, (SUM(t1.sum_sales) OVER (PARTITION BY t1.i_category, t1.i_brand, t1.s_store_name, t1.s_company_name RANGE BETWEEN UNBOUNDED PRECEDING AND UNBOUNDED FOLLOWING)) / (COUNT(t1.sum_sales) OVER (PARTITION BY t1.i_category, t1.i_brand, t1.s_store_name, t1.s_company_name RANGE BETWEEN UNBOUNDED PRECEDING AND UNBOUNDED FOLLOWING)) AS avg_monthly_sales FROM (SELECT item.i_category, item.i_class, item.i_brand, store.s_store_name, store.s_company_name, date_dim.d_moy, SUM(store_sales.ss_sales_price) AS sum_sales FROM item, store_sales, date_dim, store WHERE store_sales.ss_item_sk = item.i_item_sk AND store_sales.ss_sold_date_sk = date_dim.d_date_sk AND store_sales.ss_store_sk = store.s_store_sk AND date_dim.d_year = 2001 AND ((item.i_category = 'Books' OR item.i_category = 'Children' OR item.i_category = 'Electronics') AND (item.i_class = 'history' OR item.i_class = 'school-uniforms' OR item.i_class = 'audio') OR (item.i_category = 'Men' OR item.i_category = 'Sports' OR item.i_category = 'Shoes') AND (item.i_class = 'pants' OR item.i_class = 'tennis' OR item.i_class = 'womens')) GROUP BY item.i_brand, item.i_class, item.i_category, date_dim.d_moy, store.s_store_name, store.s_company_name) AS t1) AS t2 WHERE CASE WHEN avg_monthly_sales <> 0 THEN CAST(ABS(sum_sales - avg_monthly_sales) / avg_monthly_sales AS DECIMAL(19, 0)) ELSE NULL END > 0.1 ORDER BY sum_sales - avg_monthly_sales, s_store_name LIMIT 100