SELECT call_center.cc_call_center_id AS Call_Center, call_center.cc_name AS Call_Center_Name, call_center.cc_manager AS Manager, SUM(catalog_returns.cr_net_loss) AS Returns_Loss FROM call_center INNER JOIN catalog_returns ON call_center.cc_call_center_sk = catalog_returns.cr_call_center_sk INNER JOIN (SELECT * FROM date_dim WHERE d_year = 2000 AND d_moy = 12) AS t ON catalog_returns.cr_returned_date_sk = t.d_date_sk INNER JOIN customer ON catalog_returns.cr_returning_customer_sk = customer.c_customer_sk INNER JOIN (SELECT * FROM customer_address WHERE ca_gmt_offset = -7) AS t0 ON customer.c_current_addr_sk = t0.ca_address_sk INNER JOIN (SELECT * FROM customer_demographics WHERE cd_marital_status = 'M' AND cd_education_status = 'Unknown' OR cd_marital_status = 'W' AND cd_education_status = 'Advanced Degree') AS t1 ON customer.c_current_cdemo_sk = t1.cd_demo_sk INNER JOIN (SELECT * FROM household_demographics WHERE hd_buy_potential LIKE 'Unknown%') AS t2 ON customer.c_current_hdemo_sk = t2.hd_demo_sk GROUP BY call_center.cc_call_center_id, call_center.cc_name, call_center.cc_manager, t1.cd_marital_status, t1.cd_education_status ORDER BY SUM(catalog_returns.cr_net_loss) DESC