SELECT SUM(web_sales.ws_ext_discount_amt) AS "Excess Discount Amount" FROM web_sales INNER JOIN (SELECT * FROM item WHERE i_manufact_id = 714) AS t ON web_sales.ws_item_sk = t.i_item_sk INNER JOIN (SELECT * FROM date_dim WHERE d_date >= DATE '2000-02-01' AND d_date <= (DATE '2000-02-01' + INTERVAL '90' DAY)) AS t0 ON web_sales.ws_sold_date_sk = t0.d_date_sk INNER JOIN (SELECT web_sales0.ws_item_sk, AVG(web_sales0.ws_ext_discount_amt) AS f1 FROM web_sales AS web_sales0 INNER JOIN (SELECT * FROM date_dim WHERE d_date >= DATE '2000-02-01' AND d_date <= (DATE '2000-02-01' + INTERVAL '90' DAY)) AS t1 ON web_sales0.ws_sold_date_sk = t1.d_date_sk GROUP BY web_sales0.ws_item_sk) AS t3 ON t.i_item_sk = t3.ws_item_sk AND web_sales.ws_ext_discount_amt > 1.3 * t3.f1 ORDER BY SUM(web_sales.ws_ext_discount_amt) LIMIT 100