SELECT store_sales.ss_customer_sk, SUM(CASE WHEN store_returns.sr_return_quantity IS NOT NULL THEN (store_sales.ss_quantity - store_returns.sr_return_quantity) * store_sales.ss_sales_price ELSE store_sales.ss_quantity * store_sales.ss_sales_price END) AS sumsales FROM store_sales LEFT JOIN store_returns ON store_sales.ss_item_sk = store_returns.sr_item_sk AND store_sales.ss_ticket_number = store_returns.sr_ticket_number CROSS JOIN reason WHERE store_returns.sr_reason_sk = reason.r_reason_sk AND reason.r_reason_desc = 'reason 58' GROUP BY store_sales.ss_customer_sk ORDER BY SUM(CASE WHEN store_returns.sr_return_quantity IS NOT NULL THEN (store_sales.ss_quantity - store_returns.sr_return_quantity) * store_sales.ss_sales_price ELSE store_sales.ss_quantity * store_sales.ss_sales_price END), store_sales.ss_customer_sk LIMIT 100