SELECT COUNT(t7.ws_order_number) FILTER (WHERE t7.g_0) AS "order count", MIN(t7."total shipping cost") FILTER (WHERE t7.g_1) AS "total shipping cost", MIN(t7."total net profit") FILTER (WHERE t7.g_1) AS "total net profit" FROM (SELECT web_sales.ws_order_number, SUM(web_sales.ws_ext_ship_cost) AS "total shipping cost", SUM(web_sales.ws_net_profit) AS "total net profit", GROUPING(web_sales.ws_order_number) = 0 AS g_0, GROUPING(web_sales.ws_order_number) = 1 AS g_1 FROM web_sales INNER JOIN (SELECT * FROM date_dim WHERE d_date >= DATE '2001-04-01' AND d_date <= (DATE '2001-04-01' + INTERVAL '60' DAY)) AS t ON web_sales.ws_ship_date_sk = t.d_date_sk INNER JOIN (SELECT * FROM customer_address WHERE ca_state = 'VA') AS t0 ON web_sales.ws_ship_addr_sk = t0.ca_address_sk INNER JOIN (SELECT * FROM web_site WHERE web_company_name = 'pri') AS t1 ON web_sales.ws_web_site_sk = t1.web_site_sk INNER JOIN (SELECT web_sales0.ws_order_number FROM web_sales AS web_sales0 INNER JOIN web_sales AS web_sales1 ON web_sales0.ws_order_number = web_sales1.ws_order_number AND web_sales0.ws_warehouse_sk <> web_sales1.ws_warehouse_sk) AS t2 ON web_sales.ws_order_number = t2.ws_order_number INNER JOIN (SELECT web_returns.wr_order_number FROM web_returns INNER JOIN (SELECT web_sales2.ws_order_number, web_sales2.ws_warehouse_sk AS wh1, web_sales3.ws_warehouse_sk AS wh2 FROM web_sales AS web_sales2 INNER JOIN web_sales AS web_sales3 ON web_sales2.ws_order_number = web_sales3.ws_order_number AND web_sales2.ws_warehouse_sk <> web_sales3.ws_warehouse_sk) AS t3 ON web_returns.wr_order_number = t3.ws_order_number) AS t4 ON web_sales.ws_order_number = t4.wr_order_number GROUP BY ROLLUP(web_sales.ws_order_number)) AS t7 ORDER BY COUNT(t7.ws_order_number) FILTER (WHERE t7.g_0) LIMIT 100