SELECT COUNT(*) FROM store_sales, household_demographics, time_dim, store WHERE store_sales.ss_sold_time_sk = time_dim.t_time_sk AND (store_sales.ss_hdemo_sk = household_demographics.hd_demo_sk AND store_sales.ss_store_sk = store.s_store_sk) AND (time_dim.t_hour = 8 AND time_dim.t_minute >= 30 AND (household_demographics.hd_dep_count = 0 AND store.s_store_name = 'ese')) ORDER BY COUNT(*) LIMIT 100