SELECT SUM(CASE WHEN t0.customer_sk IS NOT NULL AND t2.customer_sk IS NULL THEN 1 ELSE 0 END) AS store_only, SUM(CASE WHEN t0.customer_sk IS NULL AND t2.customer_sk IS NOT NULL THEN 1 ELSE 0 END) AS catalog_only, SUM(CASE WHEN t0.customer_sk IS NOT NULL AND t2.customer_sk IS NOT NULL THEN 1 ELSE 0 END) AS store_and_catalog FROM (SELECT store_sales.ss_customer_sk AS customer_sk, store_sales.ss_item_sk AS item_sk FROM store_sales, date_dim WHERE store_sales.ss_sold_date_sk = date_dim.d_date_sk AND date_dim.d_month_seq >= 1199 AND date_dim.d_month_seq <= 1199 + 11) AS t0 FULL JOIN (SELECT catalog_sales.cs_bill_customer_sk AS customer_sk, catalog_sales.cs_item_sk AS item_sk FROM catalog_sales, date_dim AS date_dim0 WHERE catalog_sales.cs_sold_date_sk = date_dim0.d_date_sk AND date_dim0.d_month_seq >= 1199 AND date_dim0.d_month_seq <= 1199 + 11) AS t2 ON t0.customer_sk = t2.customer_sk AND t0.item_sk = t2.item_sk LIMIT 100